# Stripe Configuration (Required for payments)
VITE_STRIPE_PUBLISHABLE_KEY=pk_test_your_stripe_publishable_key_here

# Stripe Secret Key (Add this to your Supabase Edge Function environment)
# STRIPE_SECRET_KEY=sk_test_your_stripe_secret_key_here

# Stripe Price IDs (Replace with your actual price IDs from Stripe Dashboard)
VITE_STRIPE_MONTHLY_PRICE_ID=price_1QQoKnJNcmPzuriYjJGkGjmw
VITE_STRIPE_YEARLY_PRICE_ID=price_1QQoKnJNcmPzuriYjJGkGjmx
