import { useState, useEffect } from 'react';
import { supabase } from '../lib/supabase';
import { useAuth } from './useAuth';
import { STRIPE_PRODUCTS } from '../stripe-config';

interface SubscriptionData {
  customer_id: string | null;
  subscription_id: string | null;
  subscription_status: string | null;
  price_id: string | null;
  current_period_start: number | null;
  current_period_end: number | null;
  cancel_at_period_end: boolean | null;
  payment_method_brand: string | null;
  payment_method_last4: string | null;
}

export const useSubscription = () => {
  const { user } = useAuth();
  const [subscription, setSubscription] = useState<SubscriptionData | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) {
      setSubscription(null);
      setLoading(false);
      return;
    }

    fetchSubscription();
  }, [user]);

  const fetchSubscription = async () => {
    try {
      const { data, error } = await supabase
        .from('stripe_user_subscriptions')
        .select('*')
        .maybeSingle();

      if (error) {
        console.error('Error fetching subscription:', error);
        setSubscription(null);
      } else {
        setSubscription(data);
      }
    } catch (error) {
      console.error('Error fetching subscription:', error);
      setSubscription(null);
    } finally {
      setLoading(false);
    }
  };

  const isActive = subscription?.subscription_status === 'active';
  const isPremium = isActive && subscription?.price_id;

  const getCurrentPlan = () => {
    if (!isPremium) return 'Free';

    const product = STRIPE_PRODUCTS.find(p => p.priceId === subscription?.price_id);
    return product?.name || 'Premium';
  };

  const createCheckoutSession = async (priceId: string) => {
    if (!user) throw new Error('User not authenticated');

    try {
      const { data: { session } } = await supabase.auth.getSession();
      if (!session) throw new Error('No active session');

      const response = await fetch(`${import.meta.env.VITE_SUPABASE_URL}/functions/v1/stripe-checkout`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${session.access_token}`,
        },
        body: JSON.stringify({
          price_id: priceId,
          success_url: `${window.location.origin}/success?session_id={CHECKOUT_SESSION_ID}`,
          cancel_url: `${window.location.origin}/testimonials`,
          mode: 'subscription',
        }),
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('Checkout response error:', errorText);
        throw new Error('Failed to create checkout session');
      }

      const result = await response.json();
      return result.url;
    } catch (error) {
      console.error('Checkout error:', error);
      throw error;
    }
  };

  return {
    subscription,
    loading,
    isActive,
    isPremium,
    getCurrentPlan,
    createCheckoutSession,
    refetch: fetchSubscription,
  };
};
</boltAction>

<boltAction type="file" filePath="src/context/TradeContext.tsx">
import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { useAuth } from '../hooks/useAuth';
import { useSubscription } from '../hooks/useSubscription';
import { supabase } from '../lib/supabase';

export interface Trade {
  id: string;
  user_id: string;
  pair: string;
  direction: 'long' | 'short';
  day: string;
  size: number;
  outcome: 'win' | 'loss' | 'break-even';
  profit_loss: number;
  strategy: string;
  notes?: string;
  image_url?: string;
  market: string;
  created_at: string;
}

interface TradeContextType {
  trades: Trade[];
  addTrade: (trade: Omit<Trade, 'id' | 'user_id' | 'created_at'>) => Promise<void>;
  updateTrade: (id: string, trade: Partial<Trade>) => Promise<void>;
  deleteTrade: (id: string) => Promise<void>;
  tradeCount: number;
  isFreePlan: boolean;
  loading: boolean;
}

const TradeContext = createContext<TradeContextType | undefined>(undefined);

export const TradeProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [trades, setTrades] = useState<Trade[]>([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();
  const { isPremium } = useSubscription();

  // For development purposes, we'll use mock data when there's no user
  const hasUser = !!user;

  // Load trades when user changes or on initial load for mock data
  useEffect(() => {
    if (hasUser && user) {
      loadTrades();
    } else {
      // Load mock data for development
      loadMockTrades();
    }
  }, [hasUser, user]);

  const loadMockTrades = () => {
    // Mock data for development
    const mockTrades: Trade[] = [
      {
        id: '1',
        user_id: 'mock-user',
        pair: 'EUR/USD',
        direction: 'long',
        day: 'Monday',
        size: 1.5,
        outcome: 'win',
        profit_loss: 120.5,
        strategy: 'Breakout',
        market: 'Forex',
        created_at: new Date().toISOString(),
      },
      {
        id: '2',
        user_id: 'mock-user',
        pair: 'GBP/JPY',
        direction: 'short',
        day: 'Tuesday',
        size: 2.0,
        outcome: 'loss',
        profit_loss: -75.25,
        strategy: 'Reversal',
        market: 'Forex',
        created_at: new Date(Date.now() - 86400000).toISOString(),
      },
    ];

    setTrades(mockTrades);
    setLoading(false);
  };

  const loadTrades = async () => {
    if (!user) return;

    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('trades')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false });

      if (error) {
        console.error('Error loading trades:', error);
        // Fall back to mock data on error
        loadMockTrades();
        return;
      }

      setTrades(data || []);
    } catch (error) {
      console.error('Error loading trades:', error);
      // Fall back to mock data on error
      loadMockTrades();
    } finally {
      setLoading(false);
    }
  };

  const addTrade = async (tradeData: Omit<Trade, 'id' | 'user_id' | 'created_at'>) => {
    if (hasUser && user) {
      try {
        const { data, error } = await supabase
          .from('trades')
          .insert({
            user_id: user.id,
            pair: tradeData.pair,
            direction: tradeData.direction,
            day: tradeData.day,
            size: tradeData.size,
            outcome: tradeData.outcome,
            profit_loss: tradeData.profit_loss,
            strategy: tradeData.strategy,
            market: tradeData.market,
            image_url: tradeData.image_url,
          })
          .select()
          .single();

        if (error) {
          console.error('Error adding trade:', error);
          throw error;
        }

        setTrades(prev => [data, ...prev]);
      } catch (error) {
        console.error('Error adding trade:', error);
        throw error;
      }
    } else {
      // Mock add for development
      const newTrade: Trade = {
        id: `${trades.length + 1}`,
        user_id: 'mock-user',
        pair: tradeData.pair,
        direction: tradeData.direction,
        day: tradeData.day,
        size: tradeData.size,
        outcome: tradeData.outcome,
        profit_loss: tradeData.profit_loss,
        strategy: tradeData.strategy,
        market: tradeData.market,
        image_url: tradeData.image_url,
        created_at: new Date().toISOString(),
      };

      setTrades(prev => [newTrade, ...prev]);
    }
  };

  const updateTrade = async (id: string, updatedData: Partial<Trade>) => {
    if (hasUser && user) {
      try {
        const { data, error } = await supabase
          .from('trades')
          .update({
            pair: updatedData.pair,
            direction: updatedData.direction,
            day: updatedData.day,
            size: updatedData.size,
            outcome: updatedData.outcome,
            profit_loss: updatedData.profit_loss,
            strategy: updatedData.strategy,
            market: updatedData.market,
            image_url: updatedData.image_url,
          })
          .eq('id', id)
          .eq('user_id', user.id)
          .select()
          .single();

        if (error) {
          console.error('Error updating trade:', error);
          throw error;
        }

        setTrades(prev => prev.map(trade => 
          trade.id === id ? data : trade
        ));
      } catch (error) {
        console.error('Error updating trade:', error);
        throw error;
      }
    } else {
      // Mock update for development
      setTrades(prev => prev.map(trade => 
        trade.id === id ? { ...trade, ...updatedData } : trade
      ));
    }
  };

  const deleteTrade = async (id: string) => {
    if (hasUser && user) {
      try {
        const { error } = await supabase
          .from('trades')
          .delete()
          .eq('id', id)
          .eq('user_id', user.id);

        if (error) {
          console.error('Error deleting trade:', error);
          throw error;
        }

        setTrades(prev => prev.filter(trade => trade.id !== id));
      } catch (error) {
        console.error('Error deleting trade:', error);
        throw error;
      }
    } else {
      // Mock delete for development
      setTrades(prev => prev.filter(trade => trade.id !== id));
    }
  };

  return (
    <TradeContext.Provider value={{
      trades,
      addTrade,
      updateTrade,
      deleteTrade,
      tradeCount: trades.length,
      isFreePlan: !isPremium,
      loading
    }}>
      {children}
    </TradeContext.Provider>
  );
};

export const useTrades = () => {
  const context = useContext(TradeContext);
  if (context === undefined) {
    throw new Error('useTrades must be used within a TradeProvider');
  }
  return context;
};
</boltAction>

<boltAction type="file" filePath="src/components/CandlestickChart.tsx">
import React, { useEffect, useRef } from 'react';

const CandlestickChart: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const animationRef = useRef<number>();

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Set canvas size
    canvas.width = 400;
    canvas.height = 250;

    // Generate candlestick data
    const generateCandles = () => {
      const candles = [];
      let basePrice = 100;

      for (let i = 0; i < 20; i++) {
        const change = (Math.random() - 0.5) * 4;
        const open = basePrice;
        const close = basePrice + change;
        const high = Math.max(open, close) + Math.random() * 2;
        const low = Math.min(open, close) - Math.random() * 2;

        candles.push({ open, close, high, low });
        basePrice = close;
      }

      return candles;
    };

    let candles = generateCandles();
    let animationOffset = 0;

    const drawCandle = (x: number, candle: any, width: number) => {
      const { open, close, high, low } = candle;
      const isGreen = close > open;

      // Scale prices to fit canvas
      const scale = 150 / 20; // Adjust scale as needed
      const baseline = 200;

      const openY = baseline - (open - 90) * scale;
      const closeY = baseline - (close - 90) * scale;
      const highY = baseline - (high - 90) * scale;
      const lowY = baseline - (low - 90) * scale;

      // Draw wick
      ctx.strokeStyle = isGreen ? '#10B981' : '#EF4444';
      ctx.lineWidth = 1;
      ctx.beginPath();
      ctx.moveTo(x + width / 2, highY);
      ctx.lineTo(x + width / 2, lowY);
      ctx.stroke();

      // Draw body
      ctx.fillStyle = isGreen ? '#10B981' : '#EF4444';
      const bodyHeight = Math.abs(closeY - openY);
      const bodyY = Math.min(openY, closeY);

      if (bodyHeight < 2) {
        // Doji - draw a line
        ctx.strokeStyle = isGreen ? '#10B981' : '#EF4444';
        ctx.lineWidth = 2;
        ctx.beginPath();
        ctx.moveTo(x, openY);
        ctx.lineTo(x + width, openY);
        ctx.stroke();
      } else {
        ctx.fillRect(x, bodyY, width, bodyHeight);
      }
    };

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw background grid
      ctx.strokeStyle = '#374151';
      ctx.lineWidth = 0.5;

      // Horizontal lines
      for (let i = 0; i <= 5; i++) {
        const y = (i * canvas.height) / 5;
        ctx.beginPath();
        ctx.moveTo(0, y);
        ctx.lineTo(canvas.width, y);
        ctx.stroke();
      }

      // Vertical lines
      for (let i = 0; i <= 10; i++) {
        const x = (i * canvas.width) / 10;
        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, canvas.height);
        ctx.stroke();
      }

      // Draw candles
      const candleWidth = canvas.width / candles.length - 2;
      candles.forEach((candle, index) => {
        const x = (index * (canvas.width / candles.length)) + 1;
        drawCandle(x, candle, candleWidth);
      });

      // Update animation
      animationOffset += 0.01;
      if (animationOffset > 1) {
        animationOffset = 0;
        // Add new candle and remove first one
        const lastPrice = candles[candles.length - 1].close;
        const change = (Math.random() - 0.5) * 4;
        const open = lastPrice;
        const close = lastPrice + change;
        const high = Math.max(open, close) + Math.random() * 2;
        const low = Math.min(open, close) - Math.random() * 2;

        candles.push({ open, close, high, low });
        candles.shift();
      }

      animationRef.current = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, []);

  return (
    <div className="relative">
      <canvas
        ref={canvasRef}
        className="w-full h-auto max-w-full rounded-lg"
        style={{ maxHeight: '250px' }}
      />
      <div className="absolute top-2 left-2 text-xs text-gray-400">
        EUR/USD  Live
      </div>
      <div className="absolute top-2 right-2 text-xs text-green-500 font-mono">
        1.0845 
      </div>
    </div>
  );
};

export default CandlestickChart;
</boltAction>

